@import "~/.cache/wal/colors-rofi-dark.rasi"

* {
    font:             "FiraCode Nerd Font 12";
    foreground:       @foreground;
    background-color: @background;
    highlight:        bold underline White;
}

window {
    width:            410px;
    height:           400px;
    text-color:       @foreground;
    transparency:     "real";
    padding:          10px;
    children:         [ "mainbox" ];
    orientation:      horizontal;
    anchor:           center;
    x-offset:         300px;
    background-color: @background;
    border-radius:    15px;
    border:           0px;
    spacing:          0;
}

mainbox {
    spacing:  0;
    children: [ "inputbar", "message", "listview", "mode-switcher" ];
}

message {
    padding:          5;
    background-color: rgba(0,0,0,0.6);
    border-color:     @foreground;
    font:             "Source Code Pro 8";
    border-radius:    0px;
    text-color:       rgba(255,255,255,0.7);
}

textbox {
    text-color:       rgba(255,255,255,0.7);
}

inputbar {
    padding:          11px;
    background-color: @background;
    text-color:       @foreground;
    font:             "Source Code Pro 18";
    border-color:     @foreground;
    border:           0px;
    border-radius:    15px 15px 0px 0px;
}

entry {
    text-font:  inherit;
    text-color: inherit;
    text-size:  10px;
}

prompt {
    text-font:  inherit;
    margin:     0px 0.3em 0em 0em;
    text-color: inherit;
}

case-indicator {
    text-font:  inherit;
    text-color: inherit;
}

listview {
    margin:           0px;
    padding:          10px;
    background-color: @background;
    dynamic:          false;
    border-color:     @foreground;
    lines:            10;
    border-radius:    0px 0px 10px 10px;
    border:           0px;
}

element normal.normal {
    margin:           0px;
    background-color: rgba(0,0,0,0);
    vertical-align:   0.50;
    font:             inherit;
    border-radius:    10px;
    text-color:       @foreground;
}

element alternate.normal {
    margin:           0px;
    background-color: rgba(0,0,0,0.6);
    vertical-align:   0.50;
    font:             inherit;
    border-radius:    10px;
    text-color:       @foreground;
}

element-icon {
    margin: 5;
    size:   30;
}

element selected.normal {
    border-radius:    10px;
    background-color: @selected-normal-background;
    text-color:       @foreground;
}

element-text {
    vertical-align:   0.5;
    size:             50;
    text-color:       @foreground;
}

vertb {
    expand:   false;
    children: [ "mode-switcher" ];
}

mode-switcher {
    orientation: horizontal;
    expand:      false;
    spacing:     4px;
}

button {
    margin:           4px;
    width:            2px;
    text-color:       @foreground;
    padding:          2px;
    background-color: rgba(0,0,0,0.6);
    border-radius:    10px;
    border-color:     @foreground;
    font:             "FontAwesome 14";
}

button selected.normal {
    background-color: @selected-normal-background;
    border-color:     @foreground;
}

error-message {
    padding:          1em;
    background-color: Red;
    expand:           true;
    border-color:     DarkRed;
    border:           2px;
}

